// manipulating one random 2D-point.
// <temp> is the tolerance with whch we allow an error.
// only implementet for circle form yet.
// <allowed> is the allowed distance from the form.
auto perturb(vector<float> x, vector<float> y,float temp,int length, float allowed = 2) -> tuple<vector<float>, vector<float>>
{
	int element = rand_num(re);
	float x_el = x[element];
	float y_el = y[element];
	float x1;
	float y1;
	float new_dis;
	// coordinates of the circle
	float cx = 54.26;
	float cy = 47.83;
	float r = 30.0;
	float old_dis = abs(point_distance(x_el,y_el,cx,cy)-r);
	
	// shake seems to be relevant and the optimum depends on the init data set.
	// e.g. the angled blob data set had better results with shake = 0.5 (?)
	float shake = 0.1;
	
	bool found = 0;
	bool do_bad = zero_to_one(re) < temp;
	while(!found){
		x1 = x_el + normal_plus_minus_one(re)*shake;
		y1 = y_el + normal_plus_minus_one(re)*shake;
		new_dis = abs(point_distance(x1,y1,cx,cy)-r);
		
		if(old_dis>new_dis || new_dis<allowed || do_bad){
			x[element] = x1;
			y[element] = y1;
			found = 1;
		}
	}
	
	return{ x, y};
}

float curve(float t){
	return t < 0.5 ? 2 * t * t : 1 - (-2 * t + 2)*(-2 * t + 2) / 2;
	}

// function that runs the patern
auto run_patern(vector<float> x, vector<float> y, int length, int iter = 10000000) -> tuple<vector<float>, vector<float>>
{	
	//vector<float> x1 = x;
	//vector<float> y1 = y;
	vector<float> init_values = get_stat_values(x, y, length);
	float t;
	float max_temp = 0.4;
	//int versuche = 1;
	//char versuch = 1;
	int changes = 0;
	for(int i = 0;i < iter;i++){
		t = (max_temp) * curve(((float)iter-(float)i)/(float)iter);

		auto [x1,y1] = perturb(x,y,t,length);
		
		if(check_error(init_values,x1,y1,length)){
			changes++;
			x = move(x1);
			y = move(y1);
			
// following comments were used to force the function to run <iter> iterations or 
// at least try <versuche> times in each iteration.
// forcing can lead to deadlock!!!
			//versuch = 1;
		}
		/*else{
			//if(versuch%versuche!=0){
			//	i--;
				//versuch++;
		//	}else{
			//	versuch = 1;
			//}
			
		}*/
		
		// print progress
		
		if((i+1) % (int)(iter/2) == 0){
			cout << 100*(float)(i+1)/(float)iter << "%\n";
		}
	}
	// print how many manipulations were really done
	cout << "\n" << changes << " changes made\n";
	return{x,y};
}

